import {DepositsList, DepositItem, FormState} from '../interfaces/interfaces'
import {defineRange} from './calculator'
export const findIndexInDepositsList: Function = (list: DepositsList, formValues: FormState) => {
  if (formValues.code) {
    const index = list.deposits.findIndex((deposit: DepositItem) => deposit.code === formValues.code)

  }
  const paramList = list.deposits[index].param.map(value => value.period_from)
  const periodIndex = defineRange(formValues.period, paramList)
  return {index, periodIndex}
}
